import { React, useEffect, useRef, useState } from "react"
import { useInView } from "react-intersection-observer"


export default function Work(props) {
    const [animate, setAnimate] = useState(false)

    /* useEffect(() => {
        window.addEventListener("scroll", handleScroll)
        return () => window.removeEventListener("scroll", handleScroll)
    }, [])

    const handleScroll = () => {
        const worksSection = document.getElementById("main");
        const sectionTop = worksSection.offsetTop;
        const sectionBottom = sectionTop + worksSection.offsetHeight;

        if (window.pageYOffset >= sectionTop && window.pageYOffset <= sectionBottom && !animate) {
            setAnimate(true);
        }
    } */

    const [ref, inView] = useInView({
        threshold: 0.5,
        triggerOnce: true,
    })

    useEffect(() => {
        if (inView) {}
    })

    const titleRef = useRef(null)
    const animateTitle = () => {
        if (titleRef.current) {
            titleRef.current.classList.add("animate-title")
        }
    }
        

    return (
        <main id="main" className={props.darkMode ? "dark" : ""}>
            <div ref={ref} className="section">
                <h1 ref={titleRef} className={`main-title ${animate ? "animate" : ""}`}>
                    {inView && animateTitle()}
                    <span>M</span>
                    <span>Y</span>
                    <span> </span>
                    <span>W</span>
                    <span>O</span>
                    <span>R</span>
                    <span>K</span>
                    <span>S</span>
                </h1>
                <div className="grid-container">
                    <div className="grid-item1"></div>
                    <div className="grid-item2"></div>
                    <div className="grid-item3"></div>
                    <div className="grid-item4"></div>
                    <div className="grid-item5"></div>
                </div>
                <a href="#" className="link-work">
                    SEE ALL MY WORKS <i className="arrow">&#8594;</i>
                </a>
            </div>
        </main >
    )
}
